INSERT INTO `announcements` VALUES (1, '欢迎使用智能文档处理平台', '欢迎使用我们的智能文档处理平台！本平台提供SQL转ER图、AI论文生成、测试用例生成等多种学术工具，助力您的学习和研究工作。', 'success', 1, 1, NULL, NULL, 1, 5, '2025-08-02 17:28:42', '2025-09-15 11:55:31');
INSERT INTO `announcements` VALUES (2, '平台功能介绍', '平台主要功能包括：\n1. SQL转ER图生成工具\n2. AI智能论文生成器\n3. 测试用例自动生成\n4. 论文答辩问题生成\n5. 智能图表生成器\n\n如有问题请联系管理员。', 'info', 1, 0, NULL, NULL, 1, 6, '2025-08-02 17:28:42', '2025-09-15 11:55:29');

